                            {
                                !props.like? 
                                <i className="fa fa-heart-o" onClick={() => likeit(props.post_id)} style={{fontSize:'25px'}}></i>
                                : 
                                <i className="fa fa-heart" onClick={() => del_like(props.post_id)} style={{fontSize:'25px',color:'red'}}></i>
                            }
                            </a>
