function Feed_Feed(){
    const [data, setdata] = useState({});
    const [load, setLoad] = useState(false);

    const token=localStorage.getItem('token')
    const user_id=localStorage.getItem('user_info')
    const u_id=localStorage.getItem('user_info')
    useEffect(() => {
        
        console.log(token)
        if(token==null){
            return(
                <>
                </>
            )
        }
        fetch("http://127.0.0.1:8000/post/"+user_id+"/", {
            method: "GET",
            headers: {
                "Content-type": "application/json",
                Authorization:"Token "+token,
            }
          }
        )
          .then(res => res.json())
          .then(
            (result) => {
              setdata(result);
              setLoad(true);
            }
          )
      }, []
    )
    function All(){
        fetch("http://127.0.0.1:8000/post/"+user_id+"/", {
            method: "GET",
            headers: {
                "Content-type": "application/json",
                Authorization:"Token "+token,
            }
          }
        )
          .then(res => res.json())
          .then(
            (result) => {
              setdata(result);
              setLoad(true);
            }
          )
    }
    function likeit(post_id){
        fetch("http://127.0.0.1:8000/like/", {
            method: "POST",
            headers: {
                "Content-type": "application/json",
                Authorization:"Token "+token,
            },
            body:JSON.stringify({
                u_id:u_id,
                p_id:post_id,
                like:true,
            })
          }
        )
          .then(res => res.json())
          .then(
            (result) => {
                console.log(result)
                All()
            }
          )
    }
    function del_like(post_id){
        fetch("http://127.0.0.1:8000/like/"+u_id+"/"+post_id, {
            method: "DELETE",
            headers: {
                "Content-type": "application/json",
                Authorization:"Token "+token,
            }
          }
        )
          .then(res => res.json())
          .then(
            (result) => {
                console.log(result)
                All()
            }
          )
    }

    if(load==true){
        return(
            <>
            
            <I_nav/>
            <div className="container bg-light p-2">
                <div className="row">
                    <div className="col-8">
                        <div className="fbox">
                            <ul>
                            {
                            data.map(props => (
                                <div key={props.pid}>
                                <div className="box">
                                    <div className="head">
                                        <div className="circle">
                                            <img src={url_+props.your_user[0].avatar} className="circle_img"/>
                                        </div>
                                        <div className="title">
                                            {props.your_user[0].username}
                                        </div>
                                    </div>
                                    <div className="main">
                                        {props.File.split(".")[props.File.split(".").length-1]=="mp4"?
                                            <video className="video_" src={url_+props.File} controls></video>:
                                            <img className="img_" src={url_+props.File} alt=""/>
                                        }
                                        
                                    </div>
                                    <div className="social">
                                        <ul className="social-in">
                                            <li>
                                                <a className="nav-link">
                                                {
                                                    !props.like[0]? 
                                                    <i className="fa fa-heart-o" onClick={() => likeit(props.pid)} style={{fontSize:'25px'}}></i>
                                                    : 
                                                    <i className="fa fa-heart" onClick={() => del_like(props.pid)} style={{fontSize:'25px',color:'red'}}></i>
                                                }
                                                </a>
                                            </li>
                                            <li>
                                                <a className="nav-link" href="#">
                                                    <i className="fa fa-comments-o" style={{fontSize:'25px'}}></i>
                                                </a>
                                            </li>
                                            <li>
                                                <a className="nav-link" href="#">
                                                    <i className="fa fa-share-square-o" style={{fontSize:'25px'}}></i>
                                                </a>
                                            </li>
                                        </ul>
                                    </div>
                                    <div className="data">
                                    {props.your_user[0].username}:<span>
                                        {props.pdata}
                                        </span>
                                    </div>
                                </div>
                                </div>
                            ))}
                            </ul>
                        </div>
                    </div>
                    <I_feed2/>
                </div>
            </div>
            </>
        )
    }
    else{
        return(
            <>
            <h4>
            Unauthorized Access
            </h4>
            </>
        )
    }
}

export default Feed_Feed;
