import {React,useState,useEffect} from 'react';
import './App3.css';

function I_nav(){
    function logout(){
        const token=localStorage.getItem('token')
        fetch("http://127.0.0.1:8000/logout/", {
            method: "GET",
            headers: {
                "Content-type": "application/json",
                Authorization:"Token "+token,
            }
          }
        )
          .then(res => res.json())
          .then(
            (result) => {
                if(result.logout=='Done'){
                    localStorage.setItem('token',null)
                    window.location.href="/"
                }
            }
          )
    }
    return(
        <>
        <div className="container mb-5">
            <nav className="navbar navbar-expand-lg p-1 navbar-light bg-light">
                <a className="navbar-brand" href="#">
                <i class="fa fa-instagram" aria-hidden="true" style={{fontSize:'55px'}}></i>
                </a>
                
                <a className="navbar-brand" href="#">
                    <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/2/2a/Instagram_logo.svg/1200px-Instagram_logo.svg.png" alt="" width="150" />
                </a>
                
                
                    <ul className="navbar-nav">
                    <li className="nav-item mx-2">
                        <div className="nav-link">
                        Welcome {localStorage.getItem('user_name')}
                        </div>
                    </li>
                    <li className="nav-item mx-2">
                        <a className="nav-link" href="#">
                            <i className="fa fa-user-o" style={{fontSize:'35px'}}></i>
                        </a>
                    </li>
                    <li className="nav-item mx-2">
                        <a className="nav-link" href="#">
                            <i className="fa fa-heart-o" style={{fontSize:'35px'}}></i>
                        </a>
                    </li>
                    <li className="nav-item mx-2">
                        <a className="nav-link" onClick={logout}>
                            <i className="fa fa-sign-out" style={{fontSize:'35px'}}></i>
                        </a>
                    </li>

                    </ul>
                
            </nav>
        </div>
        </>
    );
}

function I_feed(props){
    const [change, setChange] = useState(true);
    function findlike(post_id){
        fetch("http://127.0.0.1:8000/like/"+post_id+"/"+localStorage.getItem('user_info')
        )
        .then(res => res.json())
        .then((like) =>{
            if(like[0]==undefined){

            }
            else{
                return
            }
        })
    }
    console.log(findlike(props.post_id))
    return(
        <>
            <div className="box">
                <div className="head">
                    <div className="circle">
                        <img src={props.avatar} className="circle_img" alt={props.avatar}/>
                    </div>
                    <div className="title">
                        {props.user}
                    </div>
                </div>
                <div className="main">
                    {props.img_url.split(".")[4]=="mp4"?
                        <video className="video_" src={props.img_url} controls></video>:
                        <img className="img_" src={props.img_url} alt=""/>
                    }
                    
                </div>
                <div className="social">
                    <ul className="social-in">
                        <li>
                            <a className="nav-link" onClick = {() => setChange(!change)}>
                            {
                            fetch("http://127.0.0.1:8000/like/"+post_id+"/"+localStorage.getItem('user_info')
                            )
                            .then(res => res.json())
                            .then((like) =>{
                                if(like[0]==undefined){

                                }
                                else{
                                    return
                                }
                            })
                        }
                            </a>
                        </li>
                        <li>
                            <a className="nav-link" href="#">
                                <i className="fa fa-comments-o" style={{fontSize:'25px'}}></i>
                            </a>
                        </li>
                        <li>
                            <a className="nav-link" href="#">
                                <i className="fa fa-share-square-o" style={{fontSize:'25px'}}></i>
                            </a>
                        </li>
                    </ul>
                </div>
                <div className="data">
                {props.user}:<span>
                    {props.desc}
                    </span>
                </div>
            </div>       
        </>
    );
}

