function Feed_Feed(){
    const [data, setdata] = useState({});
    const [load, setLoad] = useState(false);
    useEffect(() => {
        const token=localStorage.getItem('token')
        console.log(token)
        if(token=="null"){
            return(
                <>
                </>
            )
        }
        fetch("http://127.0.0.1:8000/post/", {
            method: "GET",
            headers: {
                "Content-type": "application/json",
                Authorization:"Token "+token,
            }
          }
        )
          .then(res => res.json())
          .then(
            (result) => {
              setdata(result);
              setLoad(true);
            }
          )
      }, [])
    if(load==true){
        return(
            <>
            <I_nav/>
            <div className="container bg-light p-2">
                <div className="row">
                    <div className="col-8">
                        <div className="fbox">
                            <ul>
                            {data.map(item => (
                                <I_feed
                                post_id={item.pid}
                                user={item.your_user[0].username}
                                avatar={item.your_user[0].avatar}
                                img_url={item.File}
                                desc={item.pdata}
                                />
                            ))}
                            </ul>
                        </div>
                    </div>
                    <I_feed2/>
                </div>
            </div>
            </>
        )
    }
    else{
        return(
            <>
            <h4>
            Unauthorized Access
            </h4>
            </>
        )
    }
}

export default Feed_Feed;
